import { test, expect } from '@playwright/test';
import testData from '../data/create-sales-orderData.json';
import { SalesOrderPage } from '../pages/SalesOrderPage';

test.describe('Sales Order Creation', () => {
  testData.forEach((data, index) => {
    test(`Create Sales Order - Dataset ${index + 1}`, async ({ page }) => {
      const salesOrderPage = new SalesOrderPage(page);

      await salesOrderPage.navigate();
      await salesOrderPage.clickNew();
      await salesOrderPage.fillCustomerAccount(data.customerAccount);
      await salesOrderPage.addLine();
      await salesOrderPage.fillItemNumber(data.itemNumber);
      await salesOrderPage.fillQuantity(data.quantity);
      await salesOrderPage.save();

      await expect(page.getByText('Sales order created successfully')).toBeVisible();
    });
  });
});
